    private void btnBorrar1NumeroActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnBorrar1NumeroActionPerformed
        String cadena;
        cadena = txtPantalla.getText();
        
        if (cadena.length()>0) {
            cadena = cadena.substring(0, cadena.length()-1);
            txtPantalla.setText(cadena);
        }
    }//GEN-LAST:event_btnBorrar1NumeroActionPerformed

