    private void btnIgualActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnIgualActionPerformed
        String total;
        Numero2 = txtPantalla.getText();
        if (!Numero2.equals("")) {
            total = Operacion(Numero1,Signo,Numero2);
            txtPantalla.setText(total);            
        } else{
            JOptionPane.showMessageDialog(this, "Debes ingresar un número","Error",JOptionPane.WARNING_MESSAGE);
        }
    }//GEN-LAST:event_btnIgualActionPerformed
    
    public static boolean ExistePunto(String cadena){
        boolean resultado;
        resultado = false;
        
        for (int i = 0; i < cadena.length(); i++) {
            if (cadena.substring(i, i+1).equals(".")) {
                //Captura la cadena de texto desde la posición i hasta la posición (i+1)-1
                //Ejm  cadena.substring(5,10)  Sería de 5 hasta 9
                resultado = true;break;
            }
        }
        return resultado;
    }
    
    public static String Operacion (String Numero1, String Signo, String Numero2){
        Double Total = 0.0;
        if (Signo.equals("/")) {
            Total = Double.parseDouble(Numero1)/Double.parseDouble(Numero2);
        }
        if (Signo.equals("*")) {
            Total = Double.parseDouble(Numero1)*Double.parseDouble(Numero2);
        }
        if (Signo.equals("+")) {
            Total = Double.parseDouble(Numero1)+Double.parseDouble(Numero2);
        }        
        if (Signo.equals("-")) {
            Total = Double.parseDouble(Numero1)-Double.parseDouble(Numero2);
        }        
        return Total.toString();
    }
    
